mapper.select:
select id, name, description, price, car_id
from trims
where id = #{id}

select id, name, description, price, car_id
from trims
where car_id = #{id}

select id, image
from trims_image
where trim_id = #{id}

SELECT CASE WHEN COUNT(*) > 0 THEN TRUE ELSE FALSE END
FROM trims
WHERE id = #{id}
